package io.github.abitgen.leetcode.challenge30.twenty20June._09_IsSubsequence

object IsSubsequence {
    fun isSubsequence(s: String, t: String): Boolean {
        if(s.isEmpty()) return true
        var sIndex = 0
        t.forEachIndexed { index, c ->

            if(c == s[sIndex]){
                sIndex++
                if(sIndex == s.length) return true
            }
        }

        return false
    }
}

fun main(){
    println(IsSubsequence.isSubsequence("ace", "abcde"))
    println(IsSubsequence.isSubsequence("aec", "abcde"))
    println(IsSubsequence.isSubsequence("leeeeetcode", ""))
}